props.code:
server "hello" {
  endpoint "/**" {
    response {
      body = "Hello World!"
    }
  }
}

server "example" {

  files {
    document_root = "htdocs"
  }

  spa {
    bootstrap_file = "htdocs/index.html"
    paths = ["/**"]
  }
}

server "example"{

  endpoint "/public/**"{

    proxy {
      backend {
        origin = "https://httpbin.org"
        path = "/**"
      }
    }
  }
}

server "example" {

  endpoint "/private/**" {
    access_control = ["accessToken"]

    proxy {
      backend {
        origin = "https://httpbin.org"
        path = "/**"
      }
    }
  }

  definitions {
    jwt "accessToken" {
      signature_algorithm = "RS256"
      key_file = "keys/public.pem"
    }
  }
}

api "my_api" {
  base_path = "/api/v1"

  endpoint "/login/**" {
    proxy {
      backend {
        origin = "http://identityprovider:8080"
      }
    }
  }

  endpoint "/cart/**" {
    proxy {
      url = "http://cartservice:8080/api/v1/**"
    }
  }

  endpoint "/account/{id}" {
    proxy {
      backend {
        path = "/user/${request.param.id}/info"
        origin = "http://accountservice:8080"
      }
    }
  }
}

server {
  endpoint "/" {
    proxy {
      backend {
        origin = "https://httpbin.org/"
        path = "/anything"
        set_request_headers = {
          # simple variable lookup
          x-uuid = request.id
          # template string
          user-agent = "myproxyClient/${request.headers.app-version}"
          # expressions and function calls
          x-env-user = env.USER != "" ? to_upper(env.USER) : "UNKNOWN"
        }
      }
    }
  }
}

