basic_auth "ba" {
  # ...
  error_handler "basic_auth_credentials_missing" {
    response {
      status = 403
      json_body = {
        error = "forbidden"
      }
    }
  }
}

# ...
definitions {
  beta_job "update_data" {
    # Execute once at the start of Couper and then every minute
    interval = "1m"

    request "origin" {
      url             = "/api/v1/exports/data"
      backend         = "read"
    }

    request "update" {
      url     = "/update"
      body    = backend_responses.origin.body
      backend = "write"
    }
  }

  backend "read" {
    origin     = "${env.MY_ORIGIN}"
    basic_auth = env.MY_AUTH
  }

  backend "write" {
    origin = "${env.ORIGIN_DATABASE}"
  }
}

jwt_signing_profile "myjwt" {
  signature_algorithm = "RS256"
  key_file = "priv_key.pem"
  ttl = "600s"
  claims = {
    iss = "MyAS"
    iat = unixtime()
  }
  headers = {
    kid = "my-jwk-id"
  }
}

openapi {
  file = "openapi.yaml"
  ignore_response_violations = true
}

request {
  url = "https://httpbin.org/anything"
  body = "foo"
}

saml "SSO" {
  idp_metadata_file = "idp-metadata.xml"
  sp_entity_id = env.SP_ENTITY_ID
  sp_acs_url = "http://localhost:8080/saml/acs"
  array_attributes = ["eduPersonAffiliation"] # or ["memberOf"]
}

server "couper" {
  hosts = ["*:443"]

  tls {
    server_certificate "api.example.com" {
      public_key_file = "couperServer.crt" # PEM
      private_key_file = "couperServer.key" # PEM
    }

    # mTLS

    client_certificate "IOT" {
      ca_certificate_file = "couperIntermediate.crt" # PEM
      # OR(AND!)
      leaf_certificate_file = "couperClient.crt" # PEM
    }
  }

spa {
    base_path = "/my-app" # mounts on /my-app(/**,/special)
    bootstrap_file = "./htdocs/index.html"
    paths = ["/**", "/special"]
}

# couper.hcl
spa {
    bootstrap_file = "./htdocs/index.html"
    paths = ["/**"]
    bootstrap_data = {
      url: env.MY_API_URL,
      prop: "value",
    }
}

// ...
   origin = env.ORIGIN
// ...
defaults {
  environment_variables = {
    ORIGIN = "http://localhost/"
    TIMEOUT = "3s"
  }
}

