props.code:
api "my_api" {
  base_path = "/api/v1"

  endpoint "/login/**" {
    proxy {
      backend {
        origin = "http://identityprovider:8080"
      }
    }
  }

  endpoint "/cart/**" {
    proxy {
      url = "http://cartservice:8080/api/v1/**"
    }
  }

  endpoint "/account/{id}" {
    proxy {
      backend {
        path = "/user/${request.param.id}/info"
        origin = "http://accountservice:8080"
      }
    }
  }
}

